import React, { useState } from 'react';
import '../styles/App.css'; 

const EarningInput = ({ onClose }) => {
  const [earningType, setEarningType] = useState('');
  const [earningValue, setEarningValue] = useState('');
  const [epfChecked, setEpfChecked] = useState(false);
  const [etfChecked, setEtfChecked] = useState(false);

  const handleEarningTypeChange = (e) => {
    setEarningType(e.target.value);
  };

  const handleEarningValueChange = (e) => {
    setEarningValue(e.target.value);
  };

  const handleCheckboxChange = (e) => {
    const { name, checked } = e.target;
    if (name === 'epf') {
      setEpfChecked(checked);
    } else if (name === 'etf') {
      setEtfChecked(checked);
    }
  };


  return (
    <><div className="mt-5">
      <h5 className="earnings-label">Earnings</h5>
      <p className="earnings-description">
        Allowance, Fixed Allowance, Bonus and etc.
      </p>
    </div><div className="mt-5 EarningsInput">
        {earningsInputs.map((input, index) => (
          <div key={index} className="mt-5">
            {input}
          </div>
        ))}
        <div className="mt-5">
          <AddNewAllowanceButton onClick={handleAddMore} />
        </div>
      </div><div className="mt-10 EarningInput">
        <div className="flex items-center">
          <input
            type="text"
            className="mr-2 p-2 border border-gray-300 rounded"
            value={earningType}
            onChange={handleEarningTypeChange}
            placeholder="Earning Type" />
          <input
            className="mr-2 p-2 border border-gray-300 rounded"
            value={earningValue}
            onChange={handleEarningValueChange}
            placeholder="Value" />
          <button className="pl-1 close-button" onClick={onClose}>
            <i className="fas fa-times"></i>
          </button>
          <div className="pl-5 pt-1">
            <label className=''>
              <input
                type="checkbox"
                name="epf"
                checked={epfChecked}
                onChange={handleCheckboxChange} />
              EPF/ETF
            </label>
          </div>
        </div>
        <div>

        </div>

      </div></>
  );
};

export default EarningInput;
