import React, { useState } from 'react';
import '../styles/App.css';

const DeductionsInput = ({ onClose }) => {
  const [deductionType, setDeductionType] = useState('');
  const [deductionValue, setDeductionValue] = useState('');
  const [epfChecked, setEpfChecked] = useState(false);
  const [etfChecked, setEtfChecked] = useState(false);

  const handleDeductionTypeChange = (e) => {
    setDeductionType(e.target.value);
  };

  const handleDeductionValueChange = (e) => {
    setDeductionValue(e.target.value);
  };

  const handleCheckboxChange = (e) => {
    const { name, checked } = e.target;
    if (name === 'epf') {
      setEpfChecked(checked);
    } else if (name === 'etf') {
      setEtfChecked(checked);
    }
  };

  return (

    <><div className="pt-5 block">
          <h5 className="deduction-label">Deductions</h5>
          <p className="deduction-description">
              Salary Advances, Loan Deductions and all
          </p>
      </div><div className="mt-10 DeductionsInput">
              <div className="flex items-center">
                  <input
                      type="text"
                      className="mr-2 p-2 border border-gray-300 rounded"
                      value={deductionType}
                      onChange={handleDeductionTypeChange}
                      placeholder="Deduction Type" />
                  <input
                      className="mr-2 p-2 border border-gray-300 rounded"
                      value={deductionValue}
                      onChange={handleDeductionValueChange}
                      placeholder="Value" />
                  <button className="pl-1 close-button" onClick={onClose}>
                      <i className="fas fa-times"></i>
                  </button>

              </div>
              <div>

              </div>
          </div></>
  );
};

export default DeductionsInput;
