import React, { useState } from 'react';
import '../styles/App.css';

const DeductionsInput = () => {
  const [deductions, setDeductions] = useState([{ deductionType: '', deductionValue: '', taxable: false }]);

  const handleDeductionvalChange = (index, field, value) => {
    const newDeductions = [...deductions];
    newDeductions[index][field] = value;
    setDeductions(newDeductions);
  };

  const handleAddMore = () => {
    setDeductions([...deductions, { deductionType: '', deductionValue: '', taxable: false }]);
  };

  const handleRemove = (index) => {
    const newDeductions = deductions.filter((_, i) => i !== index);
    setDeductions(newDeductions);
  };


  return (
    <div className="mt-5">
      <div>
        <h5 className="  deduction-label">Deductions</h5>
        <p className="text-gray-600 deduction-description">Salary Advances, Loan Deductions, and all.</p>
      </div>
      <div className="mt-5">
        <div className="flex items-center">
          <input
            type="text"
            className="mr-2 p-2 border border-gray-300 rounded w-1/2"
            value={deductionType}
            onChange={handleDeductionTypeChange}
            placeholder="Deduction Type"
          />
          <input
            type="text"
            className="mr-2 p-2 border border-gray-300 rounded w-1/2"
            value={deductionValue}
            onChange={handleDeductionValueChange}
            placeholder="Value"
          />
             <div className="mt-5">
          <button
            className="text-blue-500 hover:text-blue-700 font-bold py-2 px-4 rounded"
            onClick={handleAddMore}
          >
            + Add New Allowance
          </button>
        </div>

        </div>
      </div>
    </div>
  );
};

export default DeductionsInput;
