import React, { useState } from 'react';
import './App.css';
import ResetButton from './components/ResetButton';
import DeductionsInput from './components/DeductionsInput';
import AllowanceInput from './components/AllowanceInput';

function App() {
  const [basicSalary, setBasicSalary] = useState('');
  const [allowances, setAllowances] = useState([{}]);
  const [deductions, setDeductions] = useState([{}]);

  const handleBasicSalaryChange = (e) => {
    const value = e.target.value;
    if (value === '' || /^[0-9\b]+$/.test(value)) {
      setBasicSalary(value);
    }
  };

  const addAllowance = () => {
    setAllowances([...allowances, {}]);
  };

  const addDeduction = () => {
    setDeductions([...deductions, {}]);
  };

  return (
    <div className="relative min-h-screen flex items-center justify-center bg-gray-100">
      <div className="relative bg-white border border-gray-300 p-20 shadow-sm rounded-md">
        <ResetButton />
        <h1
          className="absolute font-inter font-bold"
          style={{
            width: '209px',
            height: '32px',
            top: '16px',
            left: '15px',
            fontSize: '20px',
            lineHeight: '32px',
            letterSpacing: '-0.02px',
            opacity: '1',
          }}
        >
          Calculate Your Salary
        </h1>
        <div className="mt-10">
          <h5
            className="font-inter font-semibold"
            style={{
              fontSize: '16px',
              lineHeight: '24px',
              letterSpacing: '-0.1px',
            }}
          >
            Basic Salary
          </h5>
          <input
            type="text"
            className="mt-2 p-2 border border-gray-300 rounded"
            value={basicSalary}
            onChange={handleBasicSalaryChange}
            placeholder="Enter Basic Salary"
          />
        </div>
        <div className="mt-10">
          <button onClick={addAllowance} className="mt-5 mb-5 bg-blue-500 text-white p-2 rounded">
            Add Allowance
          </button>
          {allowances.map((_, index) => (
            <AllowanceInput key={index} onClose={() => console.log('Closed')} />
          ))}
        </div>
        <div className="mt-10">
          <button onClick={addDeduction} className="mt-5 mb-5 bg-red-500 text-white p-2 rounded">
            Add Deduction
          </button>
          {deductions.map((_, index) => (
            <DeductionsInput key={index} onClose={() => console.log('Closed')} />
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
