import React, { useState } from 'react';


const EarningsInput = ({ onClose }) => {
  const [earningType, setEarningType] = useState('');
  const [earningValue, setEarningValue] = useState('');
  const [epfChecked, setEpfChecked] = useState(false);
  const [etfChecked, setEtfChecked] = useState(false);
  
  const [earningsInputs, setEarningsInputs] = useState([<EarningsInput key={0} />]);

 

  const handleEarningTypeChange = (e) => {
    setEarningType(e.target.value);
  };

  const handleEarningValueChange = (e) => {
    setEarningValue(e.target.value);
  };

  const handleCheckboxChange = (e) => {
    const { name, checked } = e.target;
    if (name === 'epf') {
      setEpfChecked(checked);
    } else if (name === 'etf') {
      setEtfChecked(checked);
    }
  };
  const handleAddMore = () => {
    const newInputs = [...earningsInputs, <EarningsInput key={earningsInputs.length} />];
    setEarningsInputs(newInputs);
  }
  
  const AddNewAllowanceButton = ({ onClick }) => (
    <button
      className="text-blue-500 hover:text-blue-700 font-bold py-2 px-4 rounded inline-block"
      onClick={onClick}
    >
      + Add New Allowance
    </button>
  );

  return (
    <> s</>
  );
};

export default EarningsInput;
