import React, { useState } from 'react';
import ResetButton from './components/ResetButton';
import EarningsInput from './components/EarningInput';
import DeductionsInput from './components/DeductionsInput';

import './styles/App.css';

function App() {
  const [basicSalary, setBasicSalary] = useState('');
  const [earnings, setEarnings] = useState([{ earningType: '', earningValue: '', epfChecked: false }]);
  const [deductions, setDeductions] = useState([{ deductionType: '', deductionValue: '', taxable: false }]);

  const handleBasicSalaryChange = (e) => {
    const value = e.target.value;
    if (value === '' || /^[0-9\b]+$/.test(value)) {
      setBasicSalary(value);
    }
  };

  const handleEarningChange = (index, field, value) => {
    const newEarnings = [...earnings];
    newEarnings[index][field] = value;
    setEarnings(newEarnings);
  };

  const handleAddEarning = () => {
    setEarnings([...earnings, { earningType: '', earningValue: '', epfChecked: false }]);
  };

  const handleRemoveEarning = (index) => {
    const newEarnings = earnings.filter((_, i) => i !== index);
    setEarnings(newEarnings);
  };

  const handleDeductionChange = (index, field, value) => {
    const newDeductions = [...deductions];
    newDeductions[index][field] = value;
    setDeductions(newDeductions);
  };

  const handleAddDeduction = () => {
    setDeductions([...deductions, { deductionType: '', deductionValue: '', taxable: false }]);
  };

  const handleRemoveDeduction = (index) => {
    const newDeductions = deductions.filter((_, i) => i !== index);
    setDeductions(newDeductions);
  };

  const handleReset = () => {
    setBasicSalary('');
    setEarnings([{ earningType: '', earningValue: '', epfChecked: false }]);
    setDeductions([{ deductionType: '', deductionValue: '', taxable: false }]);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="relative bg-white border border-gray-300 p-8 shadow-sm rounded-md">
        <h1 className="text-center text-xl font-bold mb-4">Calculate Your Salary</h1>
        <div className="mb-4">
          <h5 className="text-left text-md font-semibold mb-2">Basic Salary</h5>
          <input
            type="text"
            className="p-2 w-full border border-gray-300 rounded"
            value={basicSalary}
            onChange={handleBasicSalaryChange}
            placeholder="Enter Basic Salary"
          />
        </div>
        <div>
          <div className="mt-5">
            <h5 className="text-lg font-medium earnings-label">Earnings</h5>
            <p className="text-gray-600 earnings-description">Allowance, Fixed Allowance, Bonus and etc.</p>
          </div>
          <div className="mt-5 EarningsInput">
            {earnings.map((earning, index) => (
              <EarningInput
                key={index}
                index={index}
                earning={earning}
                handleEarningChange={handleEarningChange}
                onClose={handleRemoveEarning}
              />
            ))}
            <div className="mt-5">
              <button
                className="text-blue-500 hover:text-blue-700 font-bold py-2 px-4 rounded"
                onClick={handleAddEarning}
              >
                + Add New Allowance
              </button>
            </div>
          </div>
        </div>
        <DeductionsInput />
        <div className="mt-4 text-right">
          <ResetButton onReset={handleReset} />
        </div>
      </div>
    </div>
  );
}

export default App;
