import React, { useState } from 'react';
import ResetButton from './components/ResetButton';
import EarningsInput from './components/EarningInput';
import Deductionsinput from './components/Deductionsinput';

import './styles/App.css'; 

function App() {
  const [basicSalary, setBasicSalary] = useState('');
  const handleBasicSalaryChange = (e) => {
    const value = e.target.value;
    if (value === '' || /^[0-9\b]+$/.test(value)) {
      setBasicSalary(value);
    }
  };

  return (
    <div className=" min-h-screen flex items-center justify-center bg-gray-100">
      <div className="relative  bg-white border border-gray-300 p-36 shadow-sm rounded-md">
        <ResetButton />
        <h1 className="absolute calculate-title">Calculate Your Salary</h1>
        <div className="mr-96">
          <h5 className="text-left basic-salary-label">Basic Salary</h5>
          <input
            type="text"
            className="mt-2 p-2 basic-salary-input border border-gray-300 rounded"
            value={basicSalary}
            onChange={handleBasicSalaryChange}
            placeholder="Enter Basic Salary"
          />
        </div>
<></>
      </div>
    </div>
  );
}

export default App;
