import React, { useState } from 'react';
import ResetButton from './components/ResetButton.tsx';
import EarningsInput from './components/EarningInput.tsx';
import './styles/App.css'; 

function App() {
  const [basicSalary, setBasicSalary] = useState('');
  const [earningsInputs, setEarningsInputs] = useState([<EarningsInput key={0} />]);

  const handleBasicSalaryChange = (e) => {
    const value = e.target.value;
    if (value === '' || /^[0-9\b]+$/.test(value)) {
      setBasicSalary(value);
    }
  };


  const handleAddMore = () => {
    const newInputs = [...earningsInputs, <EarningsInput key={earningsInputs.length} />];
    setEarningsInputs(newInputs);
  }

  const AddNewAllowanceButton = ({ onClick }) => (
    <button
      className="text-blue-500 hover:text-blue-700 font-bold py-2 px-4 rounded inline-block"
      onClick={onClick}
    >
      + Add New Allowance
    </button>
  );

  return (
    <div className=" min-h-screen flex items-center justify-center bg-gray-100">
      <div className="relative  bg-white border border-gray-300 p-36 shadow-sm rounded-md">
        <ResetButton />
        <h1 className="absolute calculate-title">Calculate Your Salary</h1>
        <div className="mr-96">
          <h5 className="text-left basic-salary-label">Basic Salary</h5>
          <input
            type="text"
            className="mt-2 p-2 basic-salary-input border border-gray-300 rounded"
            value={basicSalary}
            onChange={handleBasicSalaryChange}
            placeholder="Enter Basic Salary"
          />
        </div>
        <div className="mt-5">
          <h5 className="earnings-label">Earnings</h5>
          <p className="earnings-description">
            Allowance, Fixed Allowance, Bonus and etc.
          </p>
        </div>
        <div className="mt-5 EarningsInput">
        {earningsInputs.map((input, index) => (
          <div key={index} className="mt-5">
            {input}
          </div>
        ))}
        <div className="mt-5">
          <AddNewAllowanceButton onClick={handleAddMore} />
        </div>
        </div>
        <div className="mt-5">
          <h5 className="earnings-label">Ear</h5>
          <p className="earnings-description">
            Allowance, Fixed Allowance, Bonus and etc.
          </p>
        </div>
      </div>

    </div>
  );
}

export default App;
